INSERT INTO `anuncio` (`idanuncio`, `nombre`, `descripcion`, `contenido`) VALUES
(1, 'Instrucciones del Examen de salud', 'Al iniciar sessión los estudiantes veran esta información', '<div><font face=''Tahoma'' size=''3''><span style='''' arial,verdana;='''' font-size:='''' large;='''' text-align:='''' justify;\\=''''>Deber&aacute;s completar cada uno de los siguientes pasos, no podr&aacute;s pasar a otro segmento sin antes haber completado el anterior. </span><span style='''' arial,verdana;='''' font-size:='''' large;='''' text-align:='''' justify;\\=''''></span><br></font><ul><li style='''' justify;\\=''''><b><font verdana\\='''' #000099\\='''' arial,='''' 4\\='''' color=''\\'' face=''Tahoma'' size=''3''>Paso 1</font></b></li><ul><li style='''' justify;\\=''''><font verdana\\='''' arial,='''' 4\\='''' face=''Tahoma'' size=''3''>Datos generales <br></font></li></ul><li style='''' justify;\\=''''><font face=''Tahoma'' size=''3''><span style='''' arial,verdana;\\=''''><font #000099\\='''' 4\\='''' color=''\\''>Paso 2</font></span></font></li><ul><li style='''' justify;\\=''''><font face=''Tahoma'' size=''3''><span style='''' arial,verdana;\\=''''><font 4\\=''''><u>Se te asignar&aacute; al azar uno de dos tipo de examen que realizamos</u>, compl&eacute;talo. &nbsp; <br></font></span></font></li></ul><li style='''' justify;\\=''''><font face=''Tahoma'' size=''3''><span style='''' arial,verdana;\\=''''><font #000099\\='''' 4\\='''' color=''\\''>Paso 3</font></span></font></li><ul><li style='''' justify;\\=''''><font verdana\\='''' arial,='''' 4\\='''' face=''Tahoma'' size=''3''>&nbsp;<font #ff0000\\='''' color=''\\''>Imprime tus resultados. <br></font></font></li><li style='''' justify;\\=''''><font verdana\\='''' arial,='''' 4\\='''' face=''Tahoma'' size=''3''>En la parte inferior te aparecer&aacute;n varias fechas, selecciona la opci&oacute;n mas adecuada para asistir&nbsp; a la segunda parte del examen, imprime la cita.&nbsp;</font></li></ul></ul><font verdana\\='''' arial,='''' 4\\='''' face=''Tahoma'' size=''3''>Presiona <b><font color=''#ff0000''>SIGUIENTE</font></b> para continuar<br></font></div>'),
(2, 'Instrucciones Autoevaluacion', 'Al tocarle autoevaluación los estudiantes veran esta información', '<font size=''4''>Deber&aacute;s responder este cuestionario sobre aspectos medicos, psicol&oacute;gicos y odontol&oacute;gicos. Al finalizar descarga e imprime los resultados y la cita con la fecha para asistir a la segunda parte. </font><br>'),
(3, 'Instrucciones Multifásico', 'Al tocarle multifásico los estudiantes veran esta información', '<font size=''4''>Este es un examen de Salud integral, en este momento completar&aacute;s un cuestionario&nbsp; sobre aspectos medicos, psicol&oacute;gicos y odontol&oacute;gicos. <br>Selecciona con el mouse el aspecto <b>CABEZA </b>del segmento de medicina y contin&uacute;a de esa manera.<br></font><br>'),
(4, 'Instrucciones Resultados AutoEvaluacion', 'Al finalizar sus examen ellos veran esta información', '<font size=''4''>Descarga e imprime tus resultados con sus respectivas interpretaciones y recomendaciones.<br><br>Ll&eacute;valo junto con tu cita y recibo de pago a la segunda parte de tu examen.<br><br></font>'),
(5, 'Instrucciones Dash de Multifásico', 'Al finalizar su examen podran visualizar esta información', '<b><font size=''4''>Imprime&nbsp; el cuestinario de salud.&nbsp; Si tienes problemas para completar el proceso ven a la Unidad de Salud te ayudaremos a resolverlo. Presiona <font color=''#ff0000''>siguiente</font> para asignarte fecha de examen<br></font></b><div style=''font-size: 10pt;''>&nbsp;</div>'),
(6, 'Instrucciones de Citas AutoEvaluacion', 'Impresión e instrucciones de sus citas', 'Esta Unidad te asigna la fecha para realizar tu examen de salud el cual consiste en una charla multidisiplinaria relacionada con aspectosde salud del cuestionario que has completado. Deber&aacute;s asitir al Edificio de Bienestar Estudiantil, tercer nivel.<br><br>Este examen es OBLIGATORIO, raz&oacute;n por la cual es importante que te presente el d&iacute;a establecido. Cumple con los requis&iacute;tos de la cita.<br> <br>De no poder asistir, acude a la Unidad de Salud para solicitar cambio de fecha.<br><br>'),
(7, 'Instrucciones no hay Citas', 'De no haber citas, informar pasos a seguir', '<div><font face=''Arial, Verdana'' size=''4''><br></font></div><font face=''Arial, Verdana'' size=''4''><div><font face=''Arial, Verdana'' size=''4''>PR&Oacute;XIMAMENTE PODR&Aacute;S COMPLETAR LA PRIMERA PARTE DEL EXAMEN DE SALUD Y ASIGNARTE LA CITA DESDE ESTE PORTAL.<br><br></font></div>&nbsp;</font>'),
(8, 'Instrucciones de Citas Multifasico', 'Impresión e instrucciones de citas multifasico', '<div style=''text-align: justify;''>Esta Unidad te asigna la fecha para realizar el examen de salud, deber&aacute;s presentanrte en el Edificio de Bienestar Estudiantil, tercer nivel.</div><div style=''text-align: justify;''><br></div><div style=''text-align: justify;''>Este examen es OBLIGATORIO, raz&oacute;n por la cual es importante que te presente sel d&iacute;a de establecido. Favor asistir en las condiciones indicadas en la&nbsp; cita que deber&aacute;s imprimir.</div><div style=''font-size: 10pt;''>&nbsp;</div><div>De no poder asistir a tu cita, presentate a la Unidad de Salud para pedir otra fecha.</div>'),
(9, 'Instruccion de examen de salud extraordinario', 'Instrucciones para el efectuar el pago extraordinario', '<font 4\\='''' \\='''' size=''\\''><b>PASOS PARA GENERAR BOLETA DE PAGO DE EXAMEN DE SALUD 2012</b></font><br><br><ul><li>Ingresa a la PAGINA <a href=''http://estudiante.usac.edu.gt/pagousac/faces/menu/sgi_inicio.jsp''>GESTOR DE PAGOS</a></li><li>Ingresa tu USUARIO (No. de Carn&eacute;) Y PASSWORD (No sabes tu pin recup&eacute;ralo en <a href=''http://rye.usac.edu.gt/index.php''>click aqu&iacute;</a>)</li><li>Selecciona examen de salud 2012</li><li>Agregar pago</li><li>Generar orden de pago</li><li>PAGAR EN BANRURAL O G&amp;T&nbsp;</li></ul>'),
(10, 'Instruccion cita asignada', 'El estudiante ha creado su cita, o ya tenia asignada una', '<div style=''font-size: 10pt;''><span style=''font-size: large;''>IMPRIME TU CITA. DEBES PRESENTARLA EL DIA ASIGNADO.<br>S&Iacute; TU EXAMEN ES EXTEMPOR&Aacute;NEO, LLEVA LA BOLETA DE PAGO.<br>Si tienes problemas para generar tu cita, consulta personalmente con la secretarias de recepci&oacute;n de Unidad de Salud<br></span></div><div style=''font-size: 10pt;''><span style=''font-size: large;''><br></span></div><div><font size=''4''>SI NO TE PRESENTAS AL EXAMEN, DEBER&Aacute;S ACUDIR A LA UNIDAD DE SALUD PARA ASIGNARTE NUEVA FECHA.</font></div>'),
(11, 'Imprimir en la unidad de salud ', 'Mensaje que se activida, al configurar que solo se imprimen las secretarias', 'VE A LA UNIDAD DE SALUD PARA IMPRIMIR TUS RESULTADOS'),
(12, 'Anuncio por correo de citas', 'Mensaje de Cita de Salud', '<b>Examen de Salud<br></b><br>Fecha para tu examen de salud, por favor informar a sus compa&ntilde;eros universitarios de primer a&ntilde;o sobre esta evaluaci&oacute;n en salud, para que se efect&uacute;en el examen.<br><div><br></div><div>* En caso de no tener asignada esta cita omite este mensaje, asiste a la Unidad de Salud para pedir tu CITA y as&iacute; obtener tu tarjeta de salud.</div><div><br></div><div>PARA CONOCER LAS ACTIVIDADES QUE REALIZA LA UNIDAD DE SALUD TE INVITAMOS A VISITAR NUESTRA PAGINA.</div><div><br></div><div><a href=''http://usalud.usac.edu.gt/''>http://usalud.usac.edu.gt/</a></div>'),
(13, 'Grupo Familiar', 'Instrucciones de Grupo Familiar', '<font size=''3''>Puedes anotar uno &oacute; m&aacute;s familiares con los que tu vivas, los campos de parentesco, edad, g&eacute;nero y salud SON OBLIGATORIOS.<br></font><br>'),
(14, 'Ambiente Familiar', 'Instruciones del Ambiente Familiar', '(Escriba los datos más importantes de su familia o de otras personas que vivan con usted, así como los problemas en su hogar: económicos, familiares, de salud o de otra índole, especialmente aquellos que incidan en su salud o su rendimiento estudiantil).');
